$(document).on('click', "a#accept, .close, .cookie-popup-close", function(e) {
    setCookie('cookiesDirective',1,365);
    $('#cookiesdirective').animate(out,1000,function() {
        $('#cookiesdirective, #cookiecss').remove();
    });
    $('#cookie-popup').fadeOut(300, function(){
        $('#cookie-popup').remove();
    });
    $('#mb-popup-overlay').removeClass('cookie-popup').fadeOut(600);
    e.preventDefault();
});

